const COMBAT_SKILL_ID_LOOKUP = new Map(COMBAT_SKILL_LIBRARY.map((entry) => [entry.id, entry]));

export function findCombatSkillByName(name) {
    if (!name || typeof name !== "string") return null;
    const lower = name.trim().toLowerCase();
    return COMBAT_SKILL_LIBRARY.find((entry) => entry.label.toLowerCase() === lower) || null;
}

export function findCombatSkillById(id) {
    if (!id || typeof id !== "string") return null;
    return COMBAT_SKILL_ID_LOOKUP.get(id.trim()) || null;
}
